import React from 'react';
import { Button, StyleSheet, Text, View } from 'react-native';
import Questions from "./components/questions/questions.json";

const TIME_LIMIT = 5
const TITLE_STATE = 0
const QUESTION_STATE = 1

class QuizQuestion extends React.Component{
  render(){
    return(
      <View style ={styles.container}>
        <Text style = {styles.text}>{this.props.question}</Text>
        {this.props.answers.map((v,i)=>
        <Button title = {v.text} buttonStyle = {styles.button} onPress = {()=> this.props.nextQuestion(v.correct)} key = {i}></Button>)}
      </View>
    )
  }
}

class LandingPage extends React.Component{
  constructor(props){
    super(props);
    this.state = {
      score: 0,
      titleText: "Answer these questions because I made them",
      counter: 0,
      currentState: TITLE_STATE,
      currentQuestion: 0
    }
    this.timeLimit = TIME_LIMIT;
  }
  nextQuestion(correct){
    console.log("QUESTION ANSWERED")

    if(correct){
      this.setState({score: this.state.score + 1})
    }
    if(this.state.currentQuestion == question.length - 1){
        console.log("ANSWERS RECORDED")
    } else{
      clearInterval(this.timer)
      console.log(this.state.currentQuestion)
      this.setState({
        titleText: "You answered X",
        currentState: QUESTION_STATE,
              currentQuestion: this.state.currentQuestion + 1
      })
    }
  }
  // countdown(){
  //   console.log("Handling")
  //   console.log(this.state.counter)
  //   if(this.state.counter < this.timeLimit){
  //     this.setState({
  //       titleText: "Counting Down",
  //       counter: this.state.counter + 1
  //     })
  //   }else{
  //     this.setState({
  //       titleText: "Let's begin", 
  //       currentState: QUESTION_STATE,
  //       counter: 0
  //     })
  //     if(this.state.currentState == TITLE_STATE){
  //       this.timer = setInterval(()=> this.countdown(), 1000)
  //     }
  //   }

  // }
  start(){
    console.log("Booting up")
    this.setState({titleText: "Booting up the Quiz", counter: 0})
    this.timer = setInterval(()=> this.countdown(), 1000)

  }
  render(){
    return(
      <View style ={styles.container}>
        <Text style={styles.timer}>{this.timeLimit - this.state.counter}</Text>
        {((this.state.currentState === TITLE_STATE) ? 
        //div like
        <>
        <Text style = {styles.text}>{this.state.titleText}</Text>

        <Button buttonStyle = {styles.button} title= "Click here to begin" onPress ={() => this.start()}/>
        </>
        :
        <QuizQuestion answers= {questions[this.state.currentQuestion].answerOptions}  
        question={question[this.state.currentQuestion].question}
        //if the user answers correctly, proceed to the next question
        nextQuestion= {(correct)=> this.nextQuestion(correct)}></QuizQuestion>)}
        <Text style={styles.score}>
          Score: {this.state.score}
        </Text>
      </View>
    )
  }
}


function App() {
  return (
    <View style={styles.container}>
      <LandingPage></LandingPage>
       <Questions></Questions> 
    </View>
  );
}
export default App;

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#fff',
    alignItems: 'center',
    justifyContent: 'center',
  },
});
